import React, { useState, useEffect, useCallback } from "react";
import "./animations.css"; // <- Import custom animations

interface Tile {
  id: number;
  content: string;
  isFlipped: boolean;
  isMatched: boolean;
  animate: boolean;
}

const MemoryTileGame = () => {
  const [tiles, setTiles] = useState<Tile[]>([]);
  const [flippedTiles, setFlippedTiles] = useState<number[]>([]);
  const [moves, setMoves] = useState(0);
  const [matches, setMatches] = useState(0);
  const [gameOver, setGameOver] = useState(false);
  const [customImages, setCustomImages] = useState<string[]>([]);
  const [imageUploads, setImageUploads] = useState<File[]>([]);
  const [showConfetti, setShowConfetti] = useState(false);
  const [showSplash, setShowSplash] = useState(true);
  const [playerName, setPlayerName] = useState(""); // New state for player name

  const audioMatch = new Audio("./bong.mp3");
  const audioFlip = new Audio("https://www.myinstants.com/media/sounds/flip.mp3");
  const audioWin = new Audio("./win.mp3");

  const generateAvatar = useCallback(() => {
    const seed = Math.random().toString(36).substring(7);
    return `https://api.dicebear.com/9.x/fun-emoji/svg?seed=${seed}`;
  }, []);

  const generateDefaultImages = useCallback(() => {
    const numPairs = 6;
    const avatars = [];
    for (let i = 0; i < numPairs; i++) {
      avatars.push(generateAvatar());
      avatars.push(generateAvatar());
    }
    return avatars;
  }, [generateAvatar]);

  const initializeGame = useCallback(() => {
    const numPairs = 6;
    let imagePool: string[];

    if (customImages.length >= numPairs) {
      imagePool = customImages.slice(0, numPairs);
    } else if (customImages.length > 0) {
      const remaining = numPairs - customImages.length;
      const generated = Array(remaining)
        .fill(null)
        .map(() => generateAvatar());
      imagePool = [...customImages, ...generated];
    } else {
      imagePool = generateDefaultImages();
    }

    const pairs = imagePool.slice(0, numPairs);
    const duplicatedPairs = [...pairs, ...pairs];
    const shuffled = duplicatedPairs.sort(() => Math.random() - 0.5);

    const initialTiles: Tile[] = shuffled.map((content, index) => ({
      id: index,
      content,
      isFlipped: false,
      isMatched: false,
      animate: false,
    }));

    setTiles(initialTiles);
    setFlippedTiles([]);
    setMoves(0);
    setMatches(0);
    setGameOver(false);
    setShowConfetti(false);
  }, [customImages, generateAvatar, generateDefaultImages]);

  useEffect(() => {
    if (!showSplash) initializeGame();
  }, [initializeGame, showSplash]);

  useEffect(() => {
    if (matches === 6) {
      setGameOver(true);
      audioWin.play();
      setShowConfetti(true);
      setTimeout(() => setShowConfetti(false), 5000);
    }
  }, [matches]);

  const handleTileClick = (index: number) => {
    if (
      flippedTiles.length === 2 ||
      tiles[index].isFlipped ||
      tiles[index].isMatched
    )
      return;

    audioFlip.play();
    const newFlipped = [...flippedTiles, index];
    setFlippedTiles(newFlipped);

    setTiles(
      tiles.map((tile, i) =>
        i === index ? { ...tile, isFlipped: true } : tile
      )
    );

    if (newFlipped.length === 2) {
      setMoves((m) => m + 1);
      setTimeout(() => checkForMatch(newFlipped[0], newFlipped[1]), 800);
    }
  };

  const checkForMatch = (i1: number, i2: number) => {
    if (tiles[i1].content === tiles[i2].content) {
      audioMatch.play();
      setMatches((m) => m + 1);

      const newTiles = tiles.map((tile, i) =>
        i === i1 || i === i2
          ? { ...tile, isMatched: true, animate: true }
          : tile
      );
      setTiles(newTiles);

      setTimeout(() => {
        setTiles((prev) => prev.map((tile) => ({ ...tile, animate: false })));
      }, 750);
    } else {
      setTiles(
        tiles.map((tile, i) =>
          i === i1 || i === i2 ? { ...tile, isFlipped: false } : tile
        )
      );
    }
    setFlippedTiles([]);
  };

  const handleImageUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (!e.target.files) return;
    const files = Array.from(e.target.files);
    setImageUploads(files);

    Promise.all(
      files.map((file) => {
        return new Promise<string>((resolve, reject) => {
          const reader = new FileReader();
          reader.onload = (event) => {
            event.target?.result
              ? resolve(event.target.result as string)
              : reject();
          };
          reader.onerror = reject;
          reader.readAsDataURL(file);
        });
      })
    )
      .then((images) => setCustomImages((prev) => [...prev, ...images]))
      .catch(console.error);

    e.target.value = "";
  };

  const handleStartGame = () => setShowSplash(false);
  const handleRestart = () => {
    setShowSplash(true);
    setCustomImages([]);
    setImageUploads([]);
    setPlayerName(""); // Reset player name on restart
  };

  const handleNameChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setPlayerName(e.target.value);
  };

  return (
    <div
      className={`min-h-screen ${
        showSplash
          ? "bg-white"
          : "bg-gradient-to-br from-blue-300 via-purple-200 to-pink-300"
      } flex items-center justify-center`}
    >
      {showSplash ? (
        <div className="text-center p-8 space-y-6 max-w-lg w-full">
          <h1 className="text-4xl font-bold text-gray-800">Make-a-Memory</h1>
          <p className="text-gray-600">
            Add images or use random avatars!
          </p>

          {/* Player name input */}
          <div>
            <label className="block text-gray-700">Player Name (optional):</label>
            <input
              type="text"
              value={playerName}
              onChange={handleNameChange}
              placeholder="Player's name"
              className="mt-2 p-2 w-full border rounded"
            />
          </div>

          <div>
            <label className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded cursor-pointer">
              Upload Images
              <input
                type="file"
                multiple
                accept="image/*"
                onChange={handleImageUpload}
                className="hidden"
              />
            </label>
          </div>

          <div className="grid grid-cols-4 gap-2 mt-4">
            {customImages.map((img, i) => (
              <img
                key={i}
                src={img}
                alt="Preview"
                className="w-16 h-16 rounded-lg object-cover"
              />
            ))}
          </div>

          <button
            onClick={handleStartGame}
            className="mt-4 bg-blue-600 hover:bg-blue-800 text-white font-bold py-2 px-6 rounded"
          >
            Start Game
          </button>
        </div>
      ) : (
        <div className="w-full max-w-5xl flex flex-col items-center">
          {showConfetti && (
            <div className="confetti-container absolute top-0 left-0 w-full h-full pointer-events-none">
              {[...Array(200)].map((_, i) => (
                <div
                  key={i}
                  className="confetti"
                  style={{
                    left: `${Math.random() * 100}vw`,
                    animationDelay: `${Math.random() * 5}s`,
                  }}
                >
                  üéâ
                </div>
              ))}
            </div>
          )}

          {/* Display player name */}
          {playerName && (
            <div className="text-4xl font-bold text-gray-800 mb-4">
              Welcome, <span className="text-purple-700">{playerName}!</span>
            </div>
          )}

          <div className="mb-4 flex justify-between w-full px-4">
            <div className="text-gray-800">
              Moves: <b>{moves}</b> | Matches: <b>{matches}</b>
            </div>
            <button
              onClick={handleRestart}
              className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded"
            >
              Back to Splash
            </button>
          </div>

          <div className="grid grid-cols-4 gap-4">
            {tiles.map((tile, index) => (
              <div
                key={tile.id}
                className={`relative w-24 h-24 md:w-32 md:h-32 rounded-xl shadow-md transition-transform duration-500 ${
                  tile.isFlipped || tile.isMatched
                    ? "transform rotate-y-180"
                    : ""
                } ${
                  tile.animate ? "spin-on-match pop-on-match" : ""
                } cursor-pointer`}
                onClick={() => handleTileClick(index)}
              >
                <div className="absolute w-full h-full flex items-center justify-center backface-hidden">
                  <div className="bg-white border border-gray-300 rounded-xl w-full h-full flex items-center justify-center text-2xl text-gray-600">
                    ‚ùì
                  </div>
                </div>
                <div
                  className="absolute w-full h-full flex items-center justify-center backface-hidden transform rotate-y-180"
                  style={{
                    display:
                      tile.isFlipped || tile.isMatched ? "block" : "none",
                  }}
                >
                  <img
                    src={tile.content}
                    alt="Tile"
                    className="w-full h-full object-cover rounded-xl"
                  />
                </div>
              </div>
            ))}
          </div>

          {gameOver && (
            <div className="mt-8 p-4 bg-green-200 text-green-800 rounded-md text-center">
              üéâ Congratulations! You won in {moves} moves! üéâ
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default MemoryTileGame;
